import React from 'react'
import Sidebar from './Sidebar.js'
import SlideShow from './Slideshow.js'
import {
  BrowserView,
  MobileView,
  isBrowser,
  isMobile
} from "react-device-detect";

class MainContent extends React.Component {
  constructor() {
    super()
  }

  render() {
    const mobileStyle = {margin: 0, width: '500px',}
    return (
      <React.Fragment>
        <BrowserView>
          <div className='row'>
            <div className='column-1'>
              <Sidebar />
            </div>
            <div class='column'>
              <div class='content'>
                <SlideShow />
              </div>
            </div>
          </div>
        </BrowserView>

        <MobileView>
          <div style={mobileStyle}>
          <SlideShow />
          </div>
        </MobileView>
      </React.Fragment>
    )
  }
}

export default MainContent
